/* Pour les écrans de petite taille, afficher le menu comme une pile de liens */
@media (max-width: 768px) {
    .sidebar {
      position: fixed;
      top: 0;
      left: -250px; /* Cache initialement le menu */
      width: 250px;
      height: 100%;
      overflow-y: auto;
      transition: all 0.3s;
    }
    
    .sidebar.show {
      left: 0; /* Affiche le menu lorsque c'est nécessaire */
    }
    
    .sidebar-nav {
      padding-top: 20px;
    }
    
    .nav-link {
      padding: 10px 15px;
      display: block;
    }
    
    .nav-link.collapsed {
      padding: 10px 15px;
    }
    
    .nav-link i {
      margin-right: 10px;
    }
    
    .nav-content {
      padding-left: 20px;
    }
  }
