+  <script>
+    document.addEventListener("DOMContentLoaded", function() {
+        const sidebarLinks = document.querySelectorAll('.sidebar-nav .nav-link');
+
+        sidebarLinks.forEach(link => {
+            link.addEventListener('click', function() {
+                const parent = this.parentElement;
+                const submenu = parent.querySelector('.nav-content');
+                
+                if (submenu) {
+                    const isOpen = submenu.classList.contains('show');
+                    
+                    if (!isOpen) {
+                        // Fermer toutes les autres sous-listes ouvertes
+                        const openSubmenus = document.querySelectorAll('.nav-content.show');
+                        openSubmenus.forEach(sub => sub.classList.remove('show'));
+                    }
+                }
+            });
+        });
+    });
+</script>
+
 </body>

